import React, { Component, useEffect } from 'react'
import { hot } from 'react-hot-loader'
import 'bootstrap/dist/css/bootstrap.css'
import { useTimer } from 'react-timer-hook';


import './App.css'


import ReactPlayer from 'react-player'

class Video extends Component {



  dato=()=>{
   this.direccion= this.props.link
   console.log(this.direccion);
   return this.direccion;
  }
  state = {
    url: null,
    playing: true
  }

  load = url => {
    this.setState({
      url,
     })
  }

  handlePlayPause = () => {
    this.setState({ playing: !this.state.playing })
  }

  handlePlay = () => {
    console.log('onPlay')
    this.setState({ playing: true })
  }

  handlePause = () => {
    console.log('onPause')
    this.setState({ playing: false })
  }

  ref = player => {
    this.player = player
  }


  render () {

    const time = new Date();
    time.setSeconds(time.getSeconds() + 40); // 40 second timer

    const {
      seconds,
      minutes,
      isRunning,
      start,
      pause,
      resume,
      restart,
    } = useTimer({ time });



    const { url, playing} = this.state


    return (
      <div>
        <div class="container">
          <div class="row">

            <div class="col">

              <section>
                <h1>ReactPlayer Demo</h1>
                <div className='player-wrapper'>
                  <ReactPlayer
                    ref={this.ref}
                    className='react-player'
                    width='100%'
                    height='100%'
                    url={url}
                    playing={playing}
                    onPlay={this.handlePlay}
                    onPause={this.handlePause}
                    onProgress={this.handleProgress}

                  />
                </div>
              </section>


            </div>
            <div class="col">
              <div style={{ textAlign: 'center' }}>
                <h1>Temporizador</h1>
                <div style={{ fontSize: '100px' }}>
                  <span>{minutes}</span>:<span>{seconds}</span>
                </div>
                <p>{isRunning ? 'Running' : 'Not running'}</p>
                <button onClick={start}>Start</button>
                <button onClick={pause}>Pause</button>
                <button onClick={resume}>Resume</button>
                <button onClick={() => {
                  // Restarts to 40 second timer
                  const time = new Date();
                  time.setSeconds(time.getSeconds() + 40);
                  restart(time)
                }}>Restart</button>
              </div>
            </div>


        </div>
        </div>
        <div class="container ">
          <div className="margin-top ">
            <div className="centrar">
          <button onClick={() => this.load(this.props.link),start}>Iniciar</button>
          <button onClick={this.handlePlayPause}>{playing ? 'Pause' : 'Play'}</button>
          </div>
          </div>
        </div>

      </div>
    )
  }
}

export default hot(module)(Video)
