import React, { Component, useEffect } from 'react'
import { hot } from 'react-hot-loader'
import 'bootstrap/dist/css/bootstrap.css'
import Timer from './Timer'


import './App.css'


import ReactPlayer from 'react-player'

class Video extends Component {

  dato=()=>{
   this.direccion= this.props.link
   console.log(this.direccion);
   return this.direccion;
  }
  state = {
    url: null,
    playing: true
  }

  load = url => {
    this.setState({
      url,
     })


  }

  handlePlayPause = () => {
    this.setState({ playing: !this.state.playing })

  }

  handlePlay = () => {
    console.log('onPlay')
    this.setState({ playing: true })
  }

  handlePause = () => {
    console.log('onPause')
    this.setState({ playing: false })

  }

  //ref = player => {
    //this.player = player
 // }


  render () {

    const time = new Date();
    time.setSeconds(time.getSeconds() + 40); // 40 second timer

    const { url, playing} = this.state


    return (
      <div>
      <div class="container">
        <div class="row">

          <div class="col">

              <section>
                <h1>ReactPlayer Demo</h1>
                <div className='player-wrapper'>
                  <ReactPlayer
                    //ref={this.ref}
                    className='react-player'
                    width='100%'
                    height='100%'
                    url={url}
                    playing={playing}
                    onPlay={this.handlePlay}
                    onPause={this.handlePause}
                    onProgress={this.handleProgress}

                  />
                </div>
              </section>


          </div>
          <div class="col">

            <Timer iniciar={this.estado} expiryTimestamp={time}/>
          </div>


        </div>
        </div>
        <div class="container ">
          <div className="margin-top ">
            <div className="centrar">
          <button onClick={this.iniciarTimer}>Iniciar</button>
          <button onClick={this.handlePlayPause}>{playing ? 'Pause' : 'Play'}</button>
          </div>
          </div>
        </div>

      </div>
    )
  }
}

export default hot(module)(Video)
